import React, { useEffect, useState } from 'react'
import axios from 'axios';

export default function AdminApproval() {
    //thiết lập bài viết cẫn xem xét
    const [postsToReview, setPostsToReview] = useState([]);

    useEffect (() => {
        //Tìm bài viết đang chờ phê duyệt
        const fetchPosts = async () => {
            try {
              const response = await axios.get('http://localhost:3001/api/v1/posts?status=pending');
              setPostsToReview(response.data.data.posts);
            } catch (error) {
              console.error('Error fetching posts:', error);
            }
          };

        fetchPosts();
    }, []);

    //phê duyệt
    const approvePost = async (postId) => {
        try {
            // Gửi request lên server để duyệt bài viết
          await axios.put(`http://localhost:3001/api/v1/posts/approve/${postId}`);

          // Lấy lại danh sách bài viết đã duyệt sau khi duyệt bài viết
          const updatedPosts = postsToReview.filter(post => post._id !== postId);
          setPostsToReview(updatedPosts);

          alert("Bài viết đã được duyệt thành công")
        } catch (error) {
          console.error('Error approving post:', error);
        }
      };

  return (
    <div>
      <h2>Bài viết đang chờ phê duyệt</h2>
      <ul>
        {postsToReview.map(post => (
          <li key={post._id}>
            <div>
              <h3>{post.title}</h3>
              <p>{post.content}</p>
              <p>Author: {post.author}</p>
              <button onClick={() => approvePost(post._id)}>DUYỆT</button>
            </div>
          </li>
        ))}
      </ul>

    </div>
  );
}
