import React, { useEffect, useState } from 'react';
import { Button, Grid } from '@material-ui/core';
import axios from 'axios';
import Post from './Post'; 
import moment from 'moment';

export default function PostList() {
    const [news, setNews] = useState([]);


    useEffect(() => {
        async function fetchNews() {
            try {
                const response = await axios.get('http://localhost:3001/api/v1/news');
                setNews(response.data.data.news);
            } catch (error) {
                console.error('Error fetching news:', error);
            }
        }

        fetchNews();
    }, []); 


  const handleDeletePost = async (postId) => {
    try {
      // Gửi yêu cầu DELETE đến API endpoint để xóa bài post
      await axios.delete(`http://localhost:3001/api/v1/posts/${postId}`);

      // Cập nhật state để loại bỏ bài post đã xóa
      const updatedNews = news.filter(item => item._id !== postId);
      setNews(updatedNews);
    } catch (error) {
      console.error('Error deleting post:', error);
    }
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    };

    return (
        <Grid container spacing={2} alignItems='stretch' justifyContent="center">
            {news.map((item) => (
                <Grid item xs={12} sm={10} key={item.id} >
                    <Post 
                        key={item._id}
                        newId = {item._id}
                        title={item.title}
                        image={item.image}
                        content={item.content}
                        author={item.author}
                        onDelete={() => handleDeletePost(item.id)}  
                        createdAt={moment(item.createdAt).format('YYYY-MM-DD')}
                    />
                </Grid>
            ))}
            <Button variant="contained" color="primary" onClick={scrollToTop}>
                Trở về đầu trang
            </Button>
        </Grid>
    );
}
