import React, {useState } from 'react'
// import { Typography } from '@material-ui/core'

export default function ContentNews({ content }) {
  const [showFullContent, setShowFullContent] = useState(false);

  const toggleContent = () => {
    setShowFullContent(!showFullContent);
  };

  return (
    <div>
      {showFullContent ? (
        <form>
          <textarea
            value={content}
            readOnly
            style={{
              maxHeight: '350px',
              minHeight: '75px',
              width: '100%',

            }}
          />
          <button onClick={toggleContent}>Thu gọn</button>
        </form>
      ) : (
        <button onClick={toggleContent}>Xem thêm</button>
      )}
    </div>
  );
};
