import React, { useEffect, useState } from 'react'
import axios from 'axios';
import {
  Card, CardHeader, Avatar, IconButton, Typography
} from "@material-ui/core";
import AvatarPost from "./AvatarPost"
import Menu from "./Menu"

export default function AdminApproval({
  updateApprovedPosts,
  title,
  author,
  attachment,
}) {
    //thiết lập bài viết cẫn xem xét
    const [postsToReview, setPostsToReview] = useState([]);

    useEffect(() => {
        const fetchPosts = async () => {
          try {
            const response = await axios.get('http://localhost:3001/api/v1/posts?status=pending');
            setPostsToReview(response.data.data.posts);
          } catch (error) {
            console.error('Error fetching posts:', error);
          }
        };

        fetchPosts();
      }, []);

    //phê duyệt
    const approvePost = async (postId) => {
        try {
            // Gửi request lên server để duyệt bài viết
            await axios.put(`http://localhost:3001/api/v1/posts/approve/${postId}`);

          // Lấy lại danh sách bài viết đã duyệt
            const response = await axios.get('http://localhost:3001/api/v1/posts/approved');
            const approvedPostsData = response.data.data.posts;

          // Cập nhật danh sách bài viết chờ duyệt, loại bỏ bài viết đã duyệt
            const updatedPostsToReview = postsToReview.filter(post => post._id !== postId);
            setPostsToReview(updatedPostsToReview);

          // Truyền dữ liệu đã duyệt sang trang status thông qua props
            updateApprovedPosts(approvedPostsData);


          alert("Bài viết đã được duyệt thành công")
        } catch (error) {
          console.error('Lõi duyệt bài viết', error);
        }
      };

  return (
    <div>
      <Menu />
      <h2 style={{
        color:"blue",
        textAlign:'center',
        paddingTop:"1rem",
        paddingBottom:"2rem"
      }}>Bài viết đang chờ phê duyệt</h2>
      <ul>
        {postsToReview.map(post => (
          <Card style={{marginBottom:"1.5rem"}}>
            <li key={post._id}>
            <div>
              {/* <h3>{post.title}</h3> */}
              <CardHeader 
                avatar={<AvatarPost attachment={post.attachment} author={post.author} />}
                title={
                  <Typography
                    style={{
                      color: "red",
                      fontSize: "18px",
                      fontWeight: "bold",
                    }}
                  >
                    {post.author}
                  </Typography>
              }

              />
              <p>{post.content}</p>
              <p>Author: {post.author}</p>
              <button style={{margin:"10px", }} onClick={() => approvePost(post._id)}>DUYỆT</button>
            </div>
          </li>
          </Card>
          // <li key={post._id}>
          //   <div>
          //     <h3>{post.title}</h3>
          //     <p>{post.content}</p>
          //     <p>Author: {post.author}</p>
          //     <button onClick={() => approvePost(post._id)}>DUYỆT</button>
          //   </div>
          // </li>
        ))}
      </ul>

    </div>
  );
}
