import React from 'react';
import moment from 'moment';
import { IconButton, Typography } from '@material-ui/core';
import Delete from '@material-ui/icons/Delete';

export default function CommentItem({ comment, onDeleteComment }) {
  const handleDelete = () => {
    onDeleteComment(comment._id);
  };
    // const handleDeleteComment = (commentId) => {
    //   if (isOwner) {
    //     axios
    //       .delete(`http://localhost:3001/api/v1/comment/${commentId}`)
    //       .then((response) => {
    //         alert("Đã xóa thành công bình luận");
    //         window.location.reload();
    //       })
    //       .catch((error) => {
    //         // Xử lý lỗi khi gọi API (nếu cần)
    //         console.log(error); 
    //       });
    //   } else { 
    //     alert('Vui lòng không xóa bài đăng của người khác')
    //   }
    // };


  //check
  const author = localStorage.getItem("name");
  const commentOwner = author === comment.author;

  return (
    <div style={{ borderBottom: "1px solid #ddd" }}>
      <div style={{ display: "flex" }}>
        <Typography variant="h6" style={{ fontSize: "13px", marginRight: "20px" }}>
          {comment.author} 
        {commentOwner && (
            <IconButton onClick={handleDelete} style={{marginBottom: "0px", color:"brown"}}>
                <Delete />
            </IconButton>
        )}
        </Typography>
      </div>
      <div style={{ display: "flex" }}>
        <Typography style={{ fontSize: "8px" }}>
          {moment(comment.createdAt).format("YYYY-MM-DD")}
        </Typography>
      </div>
      <Typography style={{ fontSize: "13px" }} className="post-content">
          {comment.content}
        </Typography>

    </div>
  );
}
