import React, { useEffect, useState } from 'react'
import axios from 'axios';

export default function AdminApproval() {
    //thiết lập bài viết cẫn xem xét
    const [postsToReview, setPostsToReview] = useState([]);

    //thiết lập bài viết đã duyệt
    const [approvedPosts, setApprovedPosts] = useState([]);

    useEffect (() => {
        //Tìm bài viết đang chờ phê duyệt
        const fetchPosts = async () => {
            try {
              const response = await axios.get('http://localhost:3001/api/v1/posts/pending');
              setPostsToReview(response.data.data.posts);
            } catch (error) {
              console.error('Error fetching posts:', error);
            }
          };

         // Tìm bài viết đã được phê duyệt
         const fetchApprovedPosts = async () => {
            try {
              const response = await axios.get('http://localhost:3001/api/v1/posts/approved');
              setApprovedPosts(response.data.data.posts);
            } catch (error) {
              console.error('Error fetching approved posts:', error);
            }
          };

        fetchPosts();
        fetchApprovedPosts();
    }, []);

    //phê duyệt
    const approvePost = async (postId) => {
        try {
          await axios.put(`http://localhost:3001/api/v1/posts/${postId}/approve`);
          // Sau khi được phê duyệt, cập nhật bài viết
          const updatedPosts = postsToReview.filter(post => post._id !== postId);
          setPostsToReview(updatedPosts);
        } catch (error) {
          console.error('Error approving post:', error);
        }
      };

  return (
    <div>
      <h2>Bài viết đang chờ phê duyệt</h2>
      <ul>
        {postsToReview.map(post => (
          <li key={post._id}>

            <div>

              <h3>{post.title}</h3>
              <p>{post.content}</p>
              <p>Author: {post.author}</p>
              <button onClick={() => approvePost(post._id)}>DUYỆT</button>
            </div>
          </li>
        ))}
      </ul>

      <h2>Bài đăng được phê duyệt</h2>
      <ul>
        {approvedPosts.map(post => (
          <li key={post._id}>
            <div>
              <h3>{post.title}</h3>
              <p>{post.content}</p>
              <p>Author: {post.author}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
